import styled from "styled-components";
import { Box } from "@material-ui/core";

export const BoxStlyed = styled(Box)`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const CardStyled = styled(Box)`
  width: 442px;
  // height: 493px;
  border-radius: 5px;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.3);
  padding: 20px;
`;

export const HeaderStyled = styled(Box)`
  padding: 20px 0px;
  display: flex;
  position: relative;
  .title {
    font-family: Prompt;
    font-size: 22px;
    font-weight: 600;
    color: #ff5a5a;
  }
  .sub-title {
    font-family: Prompt;
    font-size: 18px;
    line-height: 0.8;
    font-weight: 600;
    color: #9b9b9b;
  }
  .caption {
    font-family: Prompt;
    font-size: 18px;
    font-weight: 300;
    line-height: 0.8;
    color: #9b9b9b;
    position: absolute;
    bottom: 0px;
  }
`;

export const ImageBox = styled(Box)`
  display: flex;
  align-items: center;
  margin-right: 20px;
`;

export const FavaouriteBox = styled(Box)`
  position: absolute;
  right: 0px;
  top: 0px;
`;

export const ContentStyled = styled(Box)`
  padding: 18px 0px;
`;

export const RoundBox = styled(Box)`
  max-width: 29px; 
  height: 30px;
  margin: 0 8px 0 0;
  padding: 1px 12px 0 11px;
  background-color: #2ecc71;
  border-radius: 50%;
  margin-right: 5px;
  position: relative;
  .text {
      width: 10px;
      color: white;
      line-height: 1.9;
  }
`;


export const SubContentStyled = styled(Box)``;

export const FooterStyled = styled(Box)``;
